// n8n Code node (JavaScript)
// Espera que el item de entrada tenga los campos del error (como tu JSON)
// Devuelve subject, bodyText y bodyHtml

function fmt(v, fallback = '‚Äî') {
  if (v === null || v === undefined || v === '') return fallback;
  return String(v);
}

function nowISO() {
  return new Date().toISOString();
}

const item = $input.first().json;

const workflowId = fmt(item.workflowId);
const workflowName = fmt(item.workflowName);
const executionId = fmt(item.executionId);
const failedNodeName = fmt(item.failedNodeName);
const errorName = fmt(item.errorName, 'Error');
const errorMessage = fmt(item.errorMessage);
const timestamp = fmt(item.timestamp, nowISO());

// Asunto
const subject = `‚ùå n8n | Fall√≥ "${workflowName}" (Exec ${executionId})`;

// Cuerpo (texto plano)
const bodyText =
`Hola üëã

