    <tr><td style="padding: 6px 10px; border: 1px solid #ddd;"><b>Nodo con error</b></td><td style="padding: 6px 10px; border: 1px solid #ddd;">${failedNodeName}</td></tr>
    <tr><td style="padding: 6px 10px; border: 1px solid #ddd;"><b>Tipo de error</b></td><td style="padding: 6px 10px; border: 1px solid #ddd;">${errorName}</td></tr>
    <tr><td style="padding: 6px 10px; border: 1px solid #ddd;"><b>Mensaje</b></td><td style="padding: 6px 10px; border: 1px solid #ddd;">${errorMessage}</td></tr>
    <tr><td style="padding: 6px 10px; border: 1px solid #ddd;"><b>Timestamp</b></td><td style="padding: 6px 10px; border: 1px solid #ddd;">${timestamp}</td></tr>
